import React, {
    Component
} from 'react';

import {
    Container,
    Row,
    Col,
    Card,
    Button,
    InputGroup,
    FormControl,
    Dropdown,
    Navbar,
    Nav,
    Image
} from "react-bootstrap";

import Cookies from "universal-cookie";
import Axios from 'axios';
import {
    MoreDetail
} from './popUp';

export default class DashBoard extends Component {
    constructor() {
        super();

        this.state = {
            cards: []
        }

    }

    block = async (email) => {
        const cookies = new Cookies();
        var url = "http://localhost:8000/api.block?uname=" + cookies.get('uname') + "&target=" + email;
        const respose = await fetch(url);
    };

    like = async (email) => {
        const cookies = new Cookies();

        var url = "http://localhost:8000/api.like?uname=" + cookies.get('uname') + "&target=" + email;
        const respose = await fetch(url);
    };

    super = async (email, image) => {
        //alert(image)
        //alert(email)
        const cookies = new Cookies();
        var url = "http://localhost:8000/api.super?uname=" + cookies.get('uname') + "&target=" + email + "&image=" + image;
        const respose = await fetch(url);

    };

    popUp = async (email) => {

        alert("show pop up")


    };



    loadImage = async () => {

        const cookies = new Cookies();
        var url = "http://localhost:8000/api.loadImgDetail?uname=" + cookies.get('uname');

        const respose = await fetch(url);
        const data = await respose.json();

        await this.setState({
            imageData: data["results"]

        });
    }

    createCards = async () => {
        var cardHtml = [];

        for (let i = 0; i < this.state.imageData.length; i++) {
            var url = "http://localhost:8000/getImage?o=" + this.state.imageData[i]['email'] + "/&i=" + this.state.imageData[i]['image'];

            cardHtml.push( <
                div className = "image-card" >
                <
                div className = "image-instance" >
                <
                img src = {
                    url
                }
                onClick = {
                    () => this.loadInfoPopUp(1020, 543545, 55575)
                }
                />

                <
                /div> <
                div className = "btn-group" >
                <
                button className = "btn"
                onClick = {
                    async () => {
                        await this.block(this.state.imageData[i]['email']);
                    }
                } > Block < /button> <
                button className = "btn"
                onClick = {
                    async () => {
                        await this.like(this.state.imageData[i]['email']);
                    }
                } > Like < /button >  <
                button className = "btn"
                onClick = {
                    async () => {
                        await this.super(this.state.imageData[i]['email'], this.state.imageData[i]['image']);
                    }
                } > Super Like < /button> <
                /div>

                <
                /div>
            )
        }
        await this.setState({
            cards: cardHtml
        });
    }
    //http://localhost:8000/getImage?o=ved@gmail.com/&i=exception%20hierarchy.png
    async componentWillMount() {


        await this.loadImage();
        await this.createCards();


        //alert(JSON.stringify(this.state.imageData));


    }

    logout = async () => {


        const cookies = new Cookies();
        cookies.remove("uname");
        localStorage.removeItem('token');

        window.location.href = "http://localhost:3000/";
    };

    async loadInfoPopUp(s, e, name) {
        // if (prevDiv !== "") {
        //   document.getElementById(prevDiv).className = "cols";
        // }
        // await this.loadData(s, e, name);
        this.setState({
            infoPopUpShow: true,
            start: s,
            end: e,
            name: name
        });
    }

    render() {
        let infoPopUpClose = () => this.setState({
            infoPopUpShow: false
        });
        return ( <
            div >
            <
            br / >
            <
            button class = "logout"
            onClick = {
                async () => {
                    await this.logout();
                }
            } > Logout < /button> <br/ > < br / >

            <
            div className = "main-container" >
            <
            center >
            <
            div className = "dashboard-container" >

            {
                this.state.cards
            }

            <
            /div>  <
            /center>  <
            /div>





            <
            MoreDetail show = {
                this.state.infoPopUpShow
            }
            onHide = {
                infoPopUpClose
            }
            orgname = {
                this.state.name
            }
            start = {
                this.state.start
            }
            end = {
                this.state.end
            }
            GeneName = {
                this.state.GeneName
            }
            LocusTag = {
                this.state.LocusTag
            }
            ProteinSequence = {
                this.state.ProteinSequence
            }
            NucSequence = {
                this.state.NucSequence
            }
            />








            <
            /div>
        );
    }
}
