      super();

      this.state = {
        loadComponent: login,
        loading:0
      };

    }




    async checkLogin() {
      return true;
    }

    async componentWillMount() {




      const v = await this.checkLogin();



      if (document.URL == "http://localhost:3001/?register") 
      {

        await this.setState({
          loadComponent: registraction,
          loading:1
        });
        alert("hello mc");

      } else if (v) {



        await this.setState({
          loadComponent: login
        });


      } else {
        await this.setState({
          loadComponent: login
        });


      }


      const cookies = new Cookies();
      cookies.set('uname', 'ved prakash', {
        path: '/'
      });
      cookies.set('password', '123456789', {
        path: '/'
      });

      console.log("uname: " + cookies.get('uname'));
      console.log("password: " + cookies.get('password'));


    }


    render() {
      if (this.state.loading === 1) {
        return(
          <div>
              {header}
              <div className="mainContainer">
                {this.state.loadComponent}  
              </div>
              {footer}
            </div>
        );

      }

      else
      {
          return(

            <div>
              {header}
              <div className="mainContainer">
                {this.state.loadComponent}  
              </div>
              {footer}
            </div>

          );
        }
    }

  }

export default LayoutFile;
