import React, {
  Component
} from 'react';
import Cookies from "universal-cookie";




import './style/style.css';
import {
  Form,
  Button,
  Card,
  Container,
  Row,
  Col
} from "react-bootstrap";

import Header from './header'
import Footer from './footer'
import Login from './login'
import LoadingPage from './loadingPage'
import Registraction from './registration';

const header = <Header/> ;
const footer = <Footer/> ;
const login = <Login/>
const registraction = <Registraction/>
const loadingPage=<LoadingPage/>

  class LayoutFile extends Component {
    constructor() {
      super();

      this.state = {
        loadComponent: login,
        loading:0
      };
    }



    async checkLogin() {
      return true;
    }
    async componentWillMount() {




      const v = await this.checkLogin();



      if (document.URL == "http://localhost:3001/?register") 
      {
        this.setState = {
          loadComponent: registraction,
          loading: 0
        };



      } else if (v) {
        this.setState = {
          loadComponent: login
        };
      } else {
        this.setState = {
          loadComponent: login
        };


      }


      const cookies = new Cookies();
      cookies.set('uname', 'ved prakash', {
        path: '/'
      });
      cookies.set('password', '123456789', {
        path: '/'
      });

      console.log("uname: " + cookies.get('uname'));
      console.log("password: " + cookies.get('password'));


    }


    render() {
      if (this.state.loading === 1) {
      return(
        <div>
        {header}





        <div className="mainContainer">

        {this.loadComponent}


        </div>







        {footer}
        </div>
      );
      }
      else{

        return(
          <div>
          {header}





          <div className="mainContainer">

          {this.loadingPage}


          </div>







          {footer}
          </div>
        );

      }
    }

  }

export default LayoutFile;
